document.addEventListener('keypress', (event) => {
    if (event.key === 'Enter') return;
    console.log(event.key);
    const keyName = event.key;
    input.innerText += keyName;
                
});

dictionary.addEventListener('click', function(event) {
    if(!event.target.classList.contains('dictionary_btn')) return;

    const value = event.target.innerText;
      

    switch(value) {
        
        case 'ESC':
            result.innerText = '';
            input.innerText = '';
        break;

        case 'MIR':
            result.innerText = descElementMir[input.innerText];
        break;

        case 'MTI':
            result.innerText = descElementMti[input.innerText];
        break;

        case 'FAQ':
            input.innerText = '';
            result.innerText = '';
            result.innerText = descElementFAQ[input.innerText];
        break;

        case 'MC':
            input.innerText = 'DE';
        break;

        case 'VISA':
            input.innerText = 'FIELD';
        break;

        case 'UPI':
            input.innerText = '';
        break;

        default:
            input.innerText += value; 
    }


}); 
